import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { TemperaturaService } from 'src/app/services/temperatura.service';
import { UtilService } from 'src/app/services/validations/util.service';

@Component({
  selector: 'app-tiempo',
  templateUrl: './tiempo.component.html',
  styleUrls: ['./tiempo.component.css']
})
export class TiempoComponent {

  formulario!: FormGroup;
  showError: boolean = false;
  mensajeError: string = "";



  constructor(private fb: FormBuilder, private _util: UtilService,
              private _tiempo: TemperaturaService ){
    this.iniciaFormulario();
  }

  /**
   * metodo que inicia y crea un formulario
   */
  iniciaFormulario(){

    this.formulario = this.fb.group({
      latitud: ['', [Validators.required]],
      longitud: ['', [Validators.required]]
    })

  }

  consultar(){
    console.log("formulario: ", this.formulario);

    this._tiempo.getEstadoTiempo(this.formulario.get('latitud')?.value, this.formulario.get('longitud')?.value)
          .subscribe( (respuesta: any) => {
            console.log("respuesta: ", respuesta);
          },
          (error:any) =>{
            this.showError = true;
            this.mensajeError = "Error checking weather";
          })
  }

}
<div class="container">
    <br>
    <h4>Check time</h4>
    <h2>Enter latitude and longitude</h2>
    <div class="col-lg-6 col-md-6 alineacion card">
        <form [formGroup]="formulario" (ngSubmit)="consultar()">
            <div class="mb-3">
              <label for="exampleInputEmail1" class="form-label">Latitude</label>
              <input type="text" class="form-control" id="exampleInputEmail1" 
              aria-describedby="emailHelp" formControlName="latitud">
                <p class="text-danger" *ngIf="formulario.get('latitud')?.invalid && formulario.get('latitud')?.touched">Write latitude please</p>
            </div>
            <div class="mb-3">
              <label for="exampleInputPassword1" class="form-label">Longitude</label>
              <input type="text" class="form-control" 
              id="exampleInputPassword1" formControlName="longitud">
              <p class="text-danger" *ngIf="formulario.get('longitud')?.invalid && formulario.get('longitud')?.touched">Write longitude please</p>
            </div>
            <button type="submit" class="btn btn-primary">Check</button>
          </form>
          <p class="text-danger" *ngIf="showError"> {{mensajeError}}</p>
    </div>
</div>
